import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import styled from '@emotion/styled'

import './HomePage.css'

const HomePageWrapper = styled.div`
  ::-moz-selection {
    /* Code for Firefox */
    color: #eb12ff;
    background: black;
  }

  ::selection {
    color: #eb12ff;
    background: black;
  }

  height: 100vh;
  font-size: 16px;
  background: linear-gradient(
    146deg,
    #000000,
    #3e0077,
    rgb(0, 133, 255),
    #00dcff
  );
  background-attachment: fixed;
  font-family: 'Helvetica Neue', 'Arial', sans-serif;
  background-image: url('/img/joel-filipe.jpg');
  background-position: 50% 50%;
  background-size: 100%;
`

const Copyright = styled.section`
  font-size: 8px;
  color: #777;
  position: absolute;
  bottom: 0;
  left: 0;
  font-weight: bold;
  right: 0;
  line-height: 1.4;
  z-index: 100;
  padding: 15px 20px;
  background: black;
  font-family: 'Roboto Mono', monospace;
  letter-spacing: 0.5px;
`

const Main = styled.main`
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  height: 100%;
  display: grid;
  z-index: 2;
  background: rgba(0, 0, 0, 0.3);
  box-sizing: border-box;
  color: black;
`

export class HomePage extends Component {
  render() {
    return (
      <HomePageWrapper>
        <Copyright>
          <div>Adam John Campbell Murray</div>
          <div>Â© Copyright 2018</div>
        </Copyright>

        <Main>
          <div className="header">
            <h1 className="header__title">Adam Murray</h1>

            <h2 className="header__subtitle">Web Developer Extraordinaire</h2>

            <div className="header__links">
              <a
                href="https://github.com/AdamMurray"
                target="_blank"
                rel="noopener noreferrer"
              >
                Github
              </a>
              <a
                href="https://www.linkedin.com/in/adam-murray-9405b44b/"
                target="_blank"
                rel="noopener noreferrer"
              >
                LinkedIn
              </a>
              <a
                href="https://twitter.com/AdamJCMurray"
                target="_blank"
                rel="noopener noreferrer"
              >
                Twitter
              </a>
            </div>

            <div className="header__buttons">
              <Link to="/cv">
                <button id="checkOutMyCv">Check out my CV</button>
              </Link>
              <a
                href="mailto:adam.john.campbell.murray@gmail.com"
                target="_blank"
                rel="noopener noreferrer"
              >
                <button id="hireMe">Hire me</button>
              </a>
            </div>
          </div>
        </Main>
      </HomePageWrapper>
    )
  }
}
